Analytics.Script:
CREATE TEMPORARY TABLE API_REQUEST_DATA USING CarbonAnalytics OPTIONS(tableName "ORG_WSO2_APIMGT_STATISTICS_REQUEST");
CREATE TEMPORARY TABLE API_REQUEST_SUMMARY USING CarbonAnalytics OPTIONS (tableName "ACME_API_REQUEST_SUMMARY", schema "hour long, tenant_domain string, api_name string, api_version string, request_count int, error_count int", primaryKeys "hour,tenant_domain,api_name,api_version" );
INSERT INTO TABLE API_REQUEST_SUMMARY SELECT roundTimestamp(first(requestTime), "HOUR") AS hour, tenantDomain AS tenant_domain, api AS api_name, version AS api_version, sum(request) AS request_count, 0 AS error_count FROM APIRequestData GROUP BY roundTimestamp(requestTime, "HOUR"),tenantDomain,api,version;
